scripts.inv["money_bag"] = 1 scripts.inv["stones_bag"] = 1 scripts.inv["food_bag"] = 1 scripts.inv["other_bag"] = 1
function scripts.inv:get_bag_string_dopelniacz(type) if not type or not scripts.inv.available_types[type] then error("Wrong input") end
local type_str = type .. "_bag" return scripts.inv.available_bags_dopelniacz[scripts.inv[type_str]] end
function scripts.inv:get_bag_string_biernik(type) if not type or not scripts.inv.available_types[type] then error("Wrong input") end
local type_str = type .. "_bag" return scripts.inv.available_bags_biernik[scripts.inv[type_str]] end

