Script.script:
function new_location_special_ui()
  amap_ui_set_dirs_trigger(nil)
end

function mapper_on()
  addMapEvent("ustaw te lokacje", "setThisLocation")
end

function shortcuts_load_profile()
  tempTimer(3.3, function() amap.shortcuts:load_shortcuts() end)
end

function shortcuts_save_profile()
  amap.shortcuts:save_shortcuts()
end

function exit_event_store_room()
  table.save(getMudletHomeDir() .. "/amap_last_curr_id.lua", amap.curr)
end

